import React, {useState, useEffect} from 'react';
import articleContent from '../components/article-data';
import ArticlesList from '../components/ArticlesList';
import NotFoundPage from './NotFoundPage';
import Comments from '../components/Comments';
import UpVotes from '../components/UpVotes';
import Tags from '../components/Tags';

const ArticlePage = ( {match} ) => {

  const name = match.params.name;
  const article = articleContent.find(article => article.name === name);

  const [articleInfo, setArticleInfo] = useState({ upvotes: 0, comments: [] });


  useEffect(() => {
          const fetchData = async () => {
              const result = await fetch(`/api/articles/${name}`);
              const body = await result.json();
              console.log(body);
              setArticleInfo(body);
          }
          fetchData();
          console.log(articleInfo.tags);
          console.log(Object.values(articleInfo.tags));
  }, [name]);

  if (!article) return <NotFoundPage />



  //console.log(Object.values(articleInfo.tags));
  //const tagArray = Object.values(articleInfo.tags);
  //console.log(tagArray);
  //console.log(typeof(tagArray));


  return (
    <>
      <h1> {articleInfo.title} </h1>
      <h2> {articleInfo.author} {articleInfo.date}</h2>
      <h3> {articleInfo.subject}</h3>
      <UpVotes articleName={name} upvotes={articleInfo.upvotes} setArticleInfo={setArticleInfo} />
      <p> This is the article body. </p>
      {article.content.map((paragraph, key) => (
          <p key={key}>{paragraph}</p>
      ))}

      <h2>Tags</h2>

      <h3>Comments</h3>
    </>

  );
}

export default ArticlePage;
